    "n=int(input(\" enter a number\"))\n",
    "i=1\n",
    "while i<=n:\n",
    "    \n",
    "    #spacing\n",
    "    space=1\n",
    "    while space<=n-i:\n",
    "        print(\" \",end=\" \")\n",
    "        space=space+1\n",
    "    \n",
    "    #increasing no\n",
    "    j=1\n",
    "    while j<=i-1:\n",
    "        print(i)\n",
    "        j=j+1\n",
    "    #decreasing no\n",
    "    p=1\n",
    "    while p<=i-1:\n",
    "        print(2(i)-2)\n",
    "        p=p+1\n",
    "    print()\n",
    "    i=i+1\n",
    "\n",
    "\n",
    "\n",
    "\n"
